mapper.update:
UPDATE Aboardcomment
SET upcnt = upcnt + 1
WHERE ser = #{value}

mapper.insert:
UPDATE auction
SET readcnt = readcnt + 1
WHERE pnum = #{pnum}

mapper.select:
select * from (
select rownum rnum,a.* from (
select * from auction where
boardid = #{boardid}
order by pnum desc) a)
where rnum between #{start}
and #{end}

SELECT * FROM auction
WHERE end = 1 AND buyid = #{loginId}
ORDER BY pnum DESC

SELECT buyid, buydate, buy
FROM abuyList
WHERE pnum = #{pnum}

SELECT nvl(count(buyid),0)
FROM abuyList
where pnum = #{pnum}
